	(63, 1, 'DTO', 'icon-m-file', 'open', 0, 1, '<h3>DTO</h3><div><pre class="prettyprint linenums lang-java">package com.acxiom.frame.dto;\r\n\r\nimport java.util.HashMap;\r\nimport java.util.List;\r\nimport java.util.Map;\r\n\r\nimport org.apache.commons.lang3.StringUtils;\r\nimport org.apache.commons.lang3.builder.ToStringBuilder;\r\n\r\nimport com.acxiom.exception.RestException;\r\nimport com.google.common.collect.Maps;\r\n\r\n@SuppressWarnings(&quot;rawtypes&quot;)\r\npublic class DTO extends HashMap {\r\n\r\n	private static final long serialVersionUID = 6803645502728454774L;\r\n\r\n	@SuppressWarnings(&quot;unchecked&quot;)\r\n	public void addTmp(String tmpName, Object tmpVal) {\r\n		put(&quot;_tmp_&quot; + tmpName, tmpVal);\r\n	}\r\n\r\n	public Object getTmpValue(String tmpName) {\r\n		return get(&quot;_tmp_&quot; + tmpName);\r\n	}\r\n\r\n	public String getTmpStringVal(String tmpName) {\r\n		return String.valueOf(get(&quot;_tmp_&quot; + tmpName));\r\n	}\r\n\r\n	public Object getValue(String key) {\r\n		if (StringUtils.isEmpty(key)) {\r\n			return null;\r\n		}\r\n		String[] codes = key.split(&quot;\\\\.&quot;);\r\n		if (codes.length == 1) {\r\n			if (!this.containsKey(key)) {\r\n				throw new RestException(646, key);\r\n			}\r\n			if (this.get(key) == null) {\r\n				return null;\r\n			}\r\n			return this.get(key);\r\n		} else {\r\n			Map map = this;\r\n			for (String code : codes) {\r\n				if (map == null) {\r\n					throw new RestException(646, key);\r\n				}\r\n				if (key.endsWith(code)) {\r\n					if (map.get(code) == null) {\r\n						throw new RestException(646, key);\r\n					}\r\n					return map.get(code);\r\n				} else {\r\n					map = (Map) map.get(code);\r\n				}\r\n			}\r\n			return null;\r\n		}\r\n	}\r\n\r\n	public String getValueAsString(String key) {\r\n		return String.valueOf(getValue(key));\r\n	}\r\n\r\n	public Integer getValueAsInteger(String key) {\r\n		return Integer.parseInt(getValueAsString(key));\r\n	}\r\n\r\n	public String getAsString(String key) {\r\n		Object obj = getValue(key);\r\n		if (obj == null) {\r\n			return null;\r\n		} else {\r\n			return String.valueOf(obj).trim();\r\n		}\r\n	}\r\n\r\n	public Integer getAsInteger(String key) {\r\n		Object obj = getValue(key);\r\n		if (obj == null) {\r\n			return null;\r\n		} else {\r\n			return (Integer) obj;\r\n		}\r\n	}\r\n\r\n	@SuppressWarnings(&quot;unchecked&quot;)\r\n	public void add(String key, Object value) {\r\n		if (StringUtils.isEmpty(key)) {\r\n			return;\r\n		}\r\n		String[] codes = key.split(&quot;\\\\.&quot;);\r\n		if (codes.length == 1) {\r\n			this.put(key, value);\r\n			return;\r\n		} else {\r\n			Map map = this;\r\n			for (String code : codes) {\r\n				if (key.endsWith(code)) {\r\n					map.put(code, value);\r\n					return;\r\n				} else {\r\n					if (!map.containsKey(code)) {\r\n						Map tmp = Maps.newHashMap();\r\n						map.put(code, tmp);\r\n						map = tmp;\r\n					} else {\r\n						map = (Map) map.get(code);\r\n					}\r\n\r\n				}\r\n			}\r\n			return;\r\n		}\r\n	}\r\n\r\n	public Map getAsMap(String key) {\r\n		Object obj = getValue(key);\r\n		if (obj == null) {\r\n			return null;\r\n		} else {\r\n			return (Map) obj;\r\n		}\r\n	}\r\n\r\n	public List getAsList(String key) {\r\n		Object obj = getValue(key);\r\n		if (obj == null) {\r\n			return null;\r\n		} else {\r\n			return (List) obj;\r\n		}\r\n	}\r\n\r\n	@SuppressWarnings(&quot;unchecked&quot;)\r\n	public Map&lt;String, Object&gt; hierarchicalMerging(String... nodes) {\r\n		Map&lt;String, Object&gt; map = this;\r\n		for (String node : nodes) {\r\n			map.putAll((Map&lt;String, Object&gt;) getValue(node));\r\n		}\r\n		return map;\r\n	}\r\n\r\n	public String toString() {\r\n		return ToStringBuilder.reflectionToString(this);\r\n	};\r\n}\r\n</pre><br /><br /></div>\r\n', 'hadong', '2014-08-20 18:19:07', '', '2015-08-14 15:12:49'),
