$(\'#audoUpdateProcutVersion\').val());\r\n			};\r\n</pre><br /><pre class="prettyprint linenums lang-js">#6. Template-details 页面上Customer下的多个字段信息没有显示到页面上，错误原因是: <span style="background-color: rgb(255, 255, 204);">ng-init=&quot;loadTemplateDetailScreen()</span> 没有载入\r\n&lt;div class=&quot;acxiom-page-container&quot; id=&quot;template-detail&quot;\r\n		ng-controller=&quot;TemplateDetailController&quot; <span style="background-color: rgb(255, 255, 204);">ng-init=&quot;loadTemplateDetailScreen()</span>&quot;&gt;\r\n 		&lt;input type=&quot;hidden&quot; id=&quot;currentPage&quot; value=&quot;subnav-template-detail&quot;&gt; \r\n		&lt;div class=&quot;row-fluid&quot;&gt;\r\n			&lt;common:navigation /&gt;\r\n		&lt;/div&gt;\r\n		此处省略N多行\r\n&lt;/div&gt;\r\n\r\n对应的Js:\r\n/**\r\n * init screen.\r\n */\r\n<span style="background-color: rgb(255, 255, 204);">$scope.loadTemplateDetailScreen </span>= function() {\r\n	$scope.initSelectItems();	\r\n	$scope.init();\r\n};\r\n\r\n/**\r\n * init customer session\r\n */\r\n$scope.initSelectItems = function() {\r\n	customerService.getCurrentCustomerFromSession();\r\n	$rootScope.$on(&quot;customerFetchedFromSession&quot;, function(event, currentCustomer){\r\n		if (currentCustomer) {\r\n			$scope.customer = currentCustomer;\r\n		}\r\n	});\r\n};\r\n\r\n$scope.init = function(){\r\n	$scope.order.orderDescription=$(\'#orderDescription\').val();\r\n	$scope.order.comments=$(\'#orderComment\').val();\r\n	$scope.order.autoUpdateProductVers = (&quot;Y&quot; === $(\'#audoUpdateProcutVersion\').val());\r\n};</pre><br /><br /><br /><p></p>\r\n', 'wengel', '2014-12-15 16:32:40', '', '2015-08-14 15:12:49'),
