	(176, 128, 'click事件hook和调整顺序', 'icon-m-file', 'open', 0, 1, '<p>click事件hook和调整顺序</p><p></p><pre class="prettyprint linenums lang-js">&lt;script type=&quot;text/javascript&quot;&gt;\r\n<span style="white-space:pre">	</span>$(document).ready(function() {\r\n<span style="white-space:pre">		</span>var arr_events = $(&quot;area#WB&quot;).data(&quot;events&quot;)[&quot;click&quot;];\r\n<span style="white-space:pre">		</span>var tj_events=$(&quot;area#WB&quot;).click(function(){\r\n<span style="white-space:pre">			</span>tj_login_click();\r\n<span style="white-space:pre">		</span>});\r\n<span style="white-space:pre">		</span>$(&quot;area#WB&quot;).click(function(){\r\n<span style="white-space:pre">			</span>$(&quot;area#WB&quot;).data(&quot;events&quot;)[&quot;click&quot;]=undefined;\r\n<span style="white-space:pre">		</span>});\r\n<span style="white-space:pre">		</span>arr_events.push(tj_events);\r\n<span style="white-space:pre">		</span>$(&quot;area#WB&quot;).click(function(){  \r\n<span style="white-space:pre">			</span>$(&quot;area#WB&quot;).data(&quot;events&quot;)[&quot;click&quot;]=arr_events;  \r\n<span style="white-space:pre">		</span>});\r\n<span style="white-space:pre">	</span>});\r\n<span style="white-space:pre">	</span>function tj_login_click(){\r\n<span style="white-space:pre">		</span>$.ajax({\r\n<span style="white-space:pre">			</span>url: &quot;&lt;%=basePath%&gt;/tongji/landingClick&quot;,\r\n<span style="white-space:pre">			</span>async: false,\r\n<span style="white-space:pre">			</span>cache: false\r\n<span style="white-space:pre">		</span>});\r\n<span style="white-space:pre">		</span>return true;\r\n<span style="white-space:pre">	</span>}\r\n    &lt;/script&gt;</pre><br /><br /><p></p>\r\n', 'hadong', '2014-11-10 14:10:29', '', '2015-08-14 15:12:49'),
