	(194, 128, 'js中自定义map集合', 'icon-m-file', 'open', 0, 1, '<p>js中自定义map集合</p><p><pre class="prettyprint linenums lang-js">function Map() {\r\n	this.elements = new Array();\r\n\r\n	//获取MAP元素个数\r\n	this.size = function() {\r\n		return this.elements.length;\r\n	};\r\n\r\n	//判断MAP是否为空\r\n	this.isEmpty = function() {\r\n		return (this.elements.length &lt; 1);\r\n	};\r\n\r\n	//删除MAP所有元素\r\n	this.clear = function() {\r\n		this.elements = new Array();\r\n	};\r\n\r\n	//向MAP中增加元素（key, value) \r\n	this.put = function(_key, _value) {\r\n		this.elements.push({\r\n			key : _key,\r\n			value : _value\r\n		});\r\n	};\r\n\r\n	//删除指定KEY的元素，成功返回True，失败返回False\r\n	this.removeByKey = function(_key) {\r\n		var bln = false;\r\n		try {\r\n			for (var i = 0; i &lt; this.elements.length; i++) {\r\n				if (this.elements[i].key == _key) {\r\n					this.elements.splice(i, 1);\r\n					return true;\r\n				}\r\n			}\r\n		} catch (e) {\r\n			bln = false;\r\n		}\r\n		return bln;\r\n	};\r\n\r\n	//删除指定VALUE的元素，成功返回True，失败返回False\r\n	this.removeByValue = function(_value) {//removeByValueAndKey\r\n		var bln = false;\r\n		try {\r\n			for (var i = 0; i &lt; this.elements.length; i++) {\r\n				if (this.elements[i].value == _value) {\r\n					this.elements.splice(i, 1);\r\n					return true;\r\n				}\r\n			}\r\n		} catch (e) {\r\n			bln = false;\r\n		}\r\n		return bln;\r\n	};\r\n\r\n	//删除指定VALUE的元素，成功返回True，失败返回False\r\n	this.removeByValueAndKey = function(_key, _value) {\r\n		var bln = false;\r\n		try {\r\n			for (var i = 0; i &lt; this.elements.length; i++) {\r\n				if (this.elements[i].value == _value\r\n						&amp;&amp; this.elements[i].key == _key) {\r\n					this.elements.splice(i, 1);\r\n					return true;\r\n				}\r\n			}\r\n		} catch (e) {\r\n			bln = false;\r\n		}\r\n		return bln;\r\n	};\r\n\r\n	//获取指定KEY的元素值VALUE，失败返回NULL\r\n	this.get = function(_key) {\r\n		try {\r\n			for (var i = 0; i &lt; this.elements.length; i++) {\r\n				if (this.elements[i].key == _key) {\r\n					return this.elements[i].value;\r\n				}\r\n			}\r\n		} catch (e) {\r\n			return false;\r\n		}\r\n		return false;\r\n	};\r\n\r\n	//获取指定索引的元素（使用element.key，element.value获取KEY和VALUE），失败返回NULL\r\n	this.element = function(_index) {\r\n		if (_index &lt; 0 || _index &gt;= this.elements.length) {\r\n			return null;\r\n		}\r\n		return this.elements[_index];\r\n	};\r\n\r\n	//判断MAP中是否含有指定KEY的元素\r\n	this.containsKey = function(_key) {\r\n		var bln = false;\r\n		try {\r\n			for (var i = 0; i &lt; this.elements.length; i++) {\r\n				if (this.elements[i].key == _key) {\r\n					bln = true;\r\n				}\r\n			}\r\n		} catch (e) {\r\n			bln = false;\r\n		}\r\n		return bln;\r\n	};\r\n\r\n	//判断MAP中是否含有指定VALUE的元素\r\n	this.containsValue = function(_value) {\r\n		var bln = false;\r\n		try {\r\n			for (var i = 0; i &lt; this.elements.length; i++) {\r\n				if (this.elements[i].value == _value) {\r\n					bln = true;\r\n				}\r\n			}\r\n		} catch (e) {\r\n			bln = false;\r\n		}\r\n		return bln;\r\n	};\r\n\r\n	//判断MAP中是否含有指定VALUE的元素\r\n	this.containsObj = function(_key, _value) {\r\n		var bln = false;\r\n		try {\r\n			for (var i = 0; i &lt; this.elements.length; i++) {\r\n				if (this.elements[i].value == _value\r\n						&amp;&amp; this.elements[i].key == _key) {\r\n					bln = true;\r\n				}\r\n			}\r\n		} catch (e) {\r\n			bln = false;\r\n		}\r\n		return bln;\r\n	};\r\n\r\n	//获取MAP中所有VALUE的数组（ARRAY）\r\n	this.values = function() {\r\n		var arr = new Array();\r\n		for (var i = 0; i &lt; this.elements.length; i++) {\r\n			arr.push(this.elements[i].value);\r\n		}\r\n		return arr;\r\n	};\r\n\r\n	//获取MAP中所有VALUE的数组（ARRAY）\r\n	this.valuesByKey = function(_key) {\r\n		var arr = new Array();\r\n		for (var i = 0; i &lt; this.elements.length; i++) {\r\n			if (this.elements[i].key == _key) {\r\n				arr.push(this.elements[i].value);\r\n			}\r\n		}\r\n		return arr;\r\n	};\r\n\r\n	//获取MAP中所有KEY的数组（ARRAY）\r\n	this.keys = function() {\r\n		var arr = new Array();\r\n		for (var i = 0; i &lt; this.elements.length; i++) {\r\n			arr.push(this.elements[i].key);\r\n		}\r\n		return arr;\r\n	};\r\n\r\n	//获取key通过value\r\n	this.keysByValue = function(_value) {\r\n		var arr = new Array();\r\n		for (var i = 0; i &lt; this.elements.length; i++) {\r\n			if (_value == this.elements[i].value) {\r\n				arr.push(this.elements[i].key);\r\n			}\r\n		}\r\n		return arr;\r\n	};\r\n\r\n	//获取MAP中所有KEY的数组（ARRAY）\r\n	this.keysRemoveDuplicate = function() {\r\n		var arr = new Array();\r\n		for (var i = 0; i &lt; this.elements.length; i++) {\r\n			var flag = true;\r\n			for (var j = 0; j &lt; arr.length; j++) {\r\n				if (arr[j] == this.elements[i].key) {\r\n					flag = false;\r\n					break;\r\n				}\r\n			}\r\n			if (flag) {\r\n				arr.push(this.elements[i].key);\r\n			}\r\n		}\r\n		return arr;\r\n	};\r\n}</pre><br /><br /></p>\r\n', 'hadong', '2014-12-08 16:26:20', '', '2015-08-14 15:12:49'),
	(195, 128, 'js取当前年月日以及月第一天', 'icon-m-file', 'open', 0, 1, '<p>js取当前年月日以及月第一天</p><p></p><pre class="prettyprint linenums lang-js">// 取当前月第一天-hadong\r\nfunction getFirstDay(split){\r\n	var myDate = new Date();\r\n    var year = myDate.getFullYear();\r\n    var month = myDate.getMonth()+1;\r\n    if (month &lt; 10){\r\n        month = &quot;0&quot; + month;\r\n    }\r\n    return year + split + month + split +&quot;01&quot;;\r\n}\r\n\r\n//取当前年月日-hadong\r\nfunction getCurDate(split){\r\n	var myDate = new Date();\r\n    var year = myDate.getFullYear();\r\n    var month = myDate.getMonth()+1;\r\n    if (month &lt; 10){\r\n        month = &quot;0&quot; + month;\r\n    }\r\n    var day = myDate.getDate();\r\n    if (day &lt;10){\r\n    	day = &quot;0&quot; + day;\r\n    }\r\n    return year + split + month + split + day;\r\n}</pre><pre class="prettyprint linenums lang-js">//取当前年月日的偏移-hadong\r\nfunction getOffsetDate(split,opt_date,num){\r\n	var myDate = new Date();\r\n	if(opt_date == &quot;year&quot;){\r\n		myDate.setFullYear(myDate.getFullYear()+num);\r\n	}\r\n    if(opt_date == &quot;month&quot;){\r\n    	myDate.setMonth(myDate.getMonth()+num);\r\n    }\r\n    if(opt_date == &quot;day&quot;){\r\n    	myDate.setDate(myDate.getDate()+num);\r\n    }\r\n    var year = myDate.getFullYear();\r\n    var month = myDate.getMonth()+1;\r\n	if (month &lt; 10){\r\n        month = &quot;0&quot; + month;\r\n    }\r\n    var day = myDate.getDate();\r\n    if (day &lt;10){\r\n    	day = &quot;0&quot; + day;\r\n    }\r\n    return year + split + month + split + day;\r\n}</pre><br /><br /><br /><p></p>\r\n', 'hadong', '2014-12-08 16:28:02', '', '2015-08-14 15:12:49'),
