	(205, 203, 'js codes', 'icon-m-file', 'open', 0, 1, '<p style="text-align: center;"><strong>js code : two array compare</strong></p><p></p><pre class="prettyprint linenums lang-js">	//validate -w\r\n	var validateInvalidNumber = function(response, elements){\r\n		var elementList = [], invalidElements = [], tmp = [], o = {};\r\n		angular.forEach(response.elementList, function(element1, index1){\r\n			elementList.push(element1.logicalNumber);\r\n		});\r\n		tmp = elements.concat(elementList);\r\n		for (var i = 0; i &lt; tmp.length; i ++) (tmp[i] in o) ? o[tmp[i]] ++ : o[tmp[i]] = 1;\r\n		for (x in o) if (o[x] == 1) invalidElements.push(x);\r\n		return invalidElements;\r\n	};</pre><br /><p><br /></p><p><strong>用户输入时，讲小写改成大写：</strong></p><p></p><pre class="prettyprint linenums lang-js" style="width: 529.140625px;">style=&quot;text-transform:uppercase&quot;</pre><br /><pre class="prettyprint linenums lang-js">&lt;input type=&quot;text&quot; id=&quot;projectNumberItem&quot; ng-model=&quot;order.projectNumber&quot; ng-disabled=&quot;processReadOnly()&quot; class=&quot;acxiom-input span11&quot; ng-change=&quot;setModifiedFlagTrue()&quot; <span style="background-color: rgb(255, 255, 153);">style=&quot;text-transform:uppercase&quot;</span>&gt;</pre><p><strong>用户输入时，需要限制个数, 使用HTML自带的限制</strong><pre class="prettyprint linenums lang-js" style="width: 529.140625px;">maxlength=&quot;4&quot;</pre></p><p></p><pre class="prettyprint linenums lang-js">&lt;input type=&quot;text&quot; id=&quot;projectNumberItem&quot; ng-model=&quot;order.projectNumber&quot; ng-disabled=&quot;processReadOnly()&quot; class=&quot;acxiom-input span11&quot; ng-change=&quot;setModifiedFlagTrue()&quot; maxlength=&quot;4&quot; style=&quot;text-transform:uppercase&quot;&gt;</pre><p></p><p><br /></p><p></p><p><strong>限制input框输入的个数，但是有一点不好是，当个数出错，无法再修改值</strong></p><p></p><pre class="prettyprint linenums lang-js">directive(&quot;limitTo&quot;, [function() {\r\n		    return {\r\n		        restrict: &quot;A&quot;,\r\n		        link: function(scope, elem, attrs) {\r\n		            var limit = parseInt(attrs.limitTo);\r\n		            angular.element(elem).on(&quot;keydown&quot;, function() {\r\n		                if (this.value.length == limit) return false;\r\n		            });\r\n		        }\r\n		    }\r\n		}</pre><br /><br /><br /><p></p><p></p>\r\n', 'wengel', '2014-12-17 15:51:48', '', '2015-08-14 15:12:49'),
	(206, 73, 'sqlserver与java数据类型对应', 'icon-m-file', 'open', 0, 1, '<div style="text-align: center;"><strong>sqlserver 与java数据类型转换的java 代码</strong></div><div style="text-align: center;"><strong><br /></strong></div><div style="text-align: left;"><strong></strong><pre class="prettyprint linenums lang-java">package com.hello.wewang;\r\n\r\nimport java.sql.Timestamp;\r\nimport java.text.ParseException;\r\nimport java.text.SimpleDateFormat;\r\nimport java.util.Date;\r\n\r\n//import com.acxiom.oes.logger.LoggerManager;\r\n\r\npublic class TimeUtil {\r\n\r\n	public static final String DATE_FORMAT_REPORT = &quot;M/d/yyyy h:mm:ss a&quot;;\r\n	public static final String DATE_FORMAT_EXPORT = &quot;yyyyMMddHHmmss&quot;;\r\n	\r\n	public static final String DATE_FORMAT = &quot;yyyy-MM-dd HH:mm:ss&quot;;\r\n	public static final String DATE_YEAR_DAY_FORMAT = &quot;yyyy-MM-dd&quot;;\r\n	public static final Timestamp DEFAULT_TIMESTAMP = new Timestamp(0);\r\n	public static final SimpleDateFormat DATE_FORMATTER = new SimpleDateFormat(DATE_FORMAT);\r\n	public static final SimpleDateFormat DATE_FORMATTER_REPORT = new SimpleDateFormat(DATE_FORMAT_REPORT);\r\n	public static final SimpleDateFormat DATE_FORMATTER_EXPORT = new SimpleDateFormat(DATE_FORMAT_EXPORT);\r\n	public static final SimpleDateFormat DATE_YEAR_DAY_FORMATTER = new SimpleDateFormat(DATE_YEAR_DAY_FORMAT);\r\n	\r\n	private TimeUtil() {\r\n\r\n	}\r\n	\r\n	/**\r\n	 * get current time for report to display\r\n	 * \r\n	 * @return\r\n	 */\r\n	public static String getCurrentTimeForReport() {\r\n		SimpleDateFormat dateFormat = new SimpleDateFormat(DATE_FORMAT_REPORT);\r\n		Date date = new Date();\r\n		return dateFormat.format(date);\r\n	}\r\n	\r\n	/**\r\n	 * get current time \r\n	 * \r\n	 * @return\r\n	 */\r\n	public static String <span style="background-color: rgb(204, 255, 255);">getCurrentTime</span>() {\r\n		SimpleDateFormat dateFormat = new SimpleDateFormat(DATE_FORMAT);\r\n		Date date = new Date();\r\n		return dateFormat.format(date);\r\n	}\r\n	\r\n	/**\r\n	 * get current date : &quot;yyyy-MM-dd&quot;\r\n	 * -w\r\n	 * @return\r\n	 */\r\n	public static Timestamp <span style="background-color: rgb(255, 255, 153);">getCurrentYearDate</span>(){\r\n			SimpleDateFormat dateFormat = new SimpleDateFormat(DATE_YEAR_DAY_FORMAT);\r\n			Date date = new Date();\r\n			try {\r\n				Date newDate = DATE_YEAR_DAY_FORMATTER.parse(dateFormat.format(date));\r\n				return Timestamp.valueOf(DATE_YEAR_DAY_FORMATTER.format(newDate));\r\n			} catch (ParseException e) {\r\n//				LoggerManager.getErrorLogger().error(e.getMessage(), e);\r\n			}\r\n			return null;\r\n		}\r\n	\r\n	/**\r\n	 * Date to timestamp\r\n	 * \r\n	 * @param date\r\n	 * @return\r\n	 */\r\n	public static Timestamp <span style="background-color: rgb(153, 255, 153);">dateToTimestamp</span>(Date date){\r\n		if(date == null){\r\n			return null;\r\n		}\r\n		return Timestamp.valueOf(DATE_FORMATTER.format(date));\r\n	}\r\n	\r\n	/**\r\n	 * String to Date\r\n	 * @param str\r\n	 * @return\r\n	 */\r\n	public static Date <span style="background-color: rgb(255, 204, 153);">stringToDate</span>(String str){\r\n		try {\r\n			return DATE_FORMATTER.parse(str);\r\n		} catch (ParseException e) {\r\n//			LoggerManager.getErrorLogger().error(e.getMessage(), e);\r\n		}\r\n		return new Date();\r\n	}\r\n\r\n	/**\r\n	 * TimeStamp to Date -w\r\n	 * @param timeStamp\r\n	 * @return\r\n	 */\r\n	public static Date <span style="background-color: rgb(255, 255, 153);">timestampToDate</span>(Timestamp timeStamp){\r\n		String str = timeStamp.toString();\r\n		try {\r\n			return DATE_FORMATTER.parse(str);\r\n		} catch (ParseException e) {\r\n//			LoggerManager.getErrorLogger().error(e.getMessage(), e);\r\n		};\r\n		return null;\r\n	}\r\n}\r\n</pre><div style="text-align: left;"><br /></div><div style="text-align: left;"><strong><br /></strong></div><strong>OES ：</strong></div><div style="text-align: left;"><strong>从SqlServer中取smallDateTime 类型的值：</strong></div><div style="text-align: left;"><strong>先用rs.getTimestamp(&quot;字段&quot;)获得值，然后再调用timesstampToDate方法（），转成Date类型</strong></div><div style="text-align: left;"><pre class="prettyprint linenums lang-js">public static Order orderInfo(ResultSet rs) throws SQLException {\r\n		Order order = new Order();\r\n		order.setOrderNumber(rs.getObject(&quot;OrderNumber&quot;) == null ? null : rs.getInt(&quot;OrderNumber&quot;));\r\n		order.setOrderVersion(rs.getObject(&quot;OrderVersion&quot;) == null ? null : rs.getInt(&quot;OrderVersion&quot;));\r\n		order.setOrderDescription(rs.getString(&quot;OrderDescription&quot;));\r\n		order.setTemplate(<span style="background-color: rgb(255, 204, 204);">&quot;Y&quot;.equalsIgnoreCase(rs.getString(&quot;IsTemplate&quot;)) ? true : false</span>);\r\n		order.setCreateUserId(rs.getString(&quot;CreateUserID&quot;));\r\n		order.setCreateDate(<span style="background-color: rgb(255, 255, 153);">rs.getObject(&quot;CreateDate&quot;) == null ? null : TimeUtil.timestampToDate(rs.getTimestamp(&quot;CreateDate&quot;))</span>); //-w\r\n		order.setLastUpdateUserId(rs.getString(&quot;LastUpdateUserID&quot;));\r\n		order.setLastUpdateDate(<span style="background-color: rgb(255, 255, 153);">rs.getObject(&quot;LastUpdateDate&quot;) == null ? null : TimeUtil.timestampToDate(rs.getTimestamp(&quot;LastUpdateDate&quot;))</span>);\r\n		order.setLastElmCompUpdateDate(rs.getObject(&quot;LastElmCompUpdateDate&quot;) == null ? null : TimeUtil.timestampToDate(rs.getTimestamp(&quot;LastElmCompUpdateDate&quot;)));\r\n		order.setDeleteUserId(rs.getString(&quot;DeleteUserID&quot;));\r\n		order.setDeleteDate(rs.getObject(&quot;DeleteDate&quot;) == null ? null : TimeUtil.timestampToDate(rs.getTimestamp(&quot;DeleteDate&quot;)));\r\n		………\r\n		return order;\r\n	}</pre><br /><br /><br /></div>\r\n', 'wengel', '2014-12-19 14:27:10', '', '2015-08-14 15:12:49'),
	(207, 203, 'input框和span', 'icon-m-file', 'open', 0, 1, '<div style="text-align: center;"><strong>input框和span</strong></div><div style="text-align: left;"><br /></div><div style="text-align: left;"><strong>###1.&nbsp;</strong>当input框输入：order“hello”， 再次打开这个页面，当前的input框中value值会变成：order，引号的部分会消失。<br /></div><div style="text-align: left;"><strong></strong><pre class="prettyprint linenums lang-js"> &lt;%-- &lt;input type=&quot;hidden&quot; id=&quot;projectNumber&quot; value=&quot;${ORDER.projectNumber}&quot;&gt; --%&gt;\r\n &lt;span id=&quot;projectNumber&quot; class=&quot;hide&quot;&gt;${ORDER.projectNumber}&lt;/span&gt;</pre><div style="text-align: left;">备注：换成&lt;span&gt;标签即可变相解决这个问题<br /></div></div><div style="text-align: left;"><strong><br /></strong></div><div style="text-align: left;"><strong><br /></strong></div><div style="text-align: left;"><strong><br /></strong></div><div style="text-align: left;"><strong>##2.&nbsp;</strong>input框输入值变大写，并且只能四位</div><div style="text-align: left;"><strong></strong><pre class="prettyprint linenums lang-js">&lt;input type=&quot;text&quot; id=&quot;projectNumberItem&quot; ng-model=&quot;order.projectNumber&quot; ng-disabled=&quot;processReadOnly()&quot; class=&quot;acxiom-input span11&quot; ng-change=&quot;setModifiedFlagTrue()&quot; <span style="background-color: rgb(255, 204, 255);">maxlength=&quot;4&quot; </span><span style="background-color: rgb(255, 255, 153);">style=&quot;text-transform:uppercase&quot;</span>&gt;</pre><div style="text-align: left;"><br /></div><div style="text-align: left;"><br /></div><strong>###3. </strong>textarea 框中只允许输入数组<pre class="prettyprint linenums lang-js">$scope.validateNumericalElements = function(){\r\n	$(\'textarea\').keypress(function(e) {\r\n		var a = [];\r\n		var k = e.which;\r\n		for (var i = 48; i &lt; 58; i++){\r\n			a.push(i); //ASCII(48-57) [0-9] \r\n		}\r\n		a.push(13); //carriage return [\\n]\r\n		if (!(a.indexOf(k)&gt;=0))\r\n			e.preventDefault();\r\n	});\r\n};</pre><div style="text-align: left;"><br /></div><strong>###4. </strong>angularjs中input ,type = checkbox, 默认值是 true or false, 如何改成string型的Y or N:</div><div style="text-align: left;"><pre class="prettyprint linenums lang-js">&lt;input type=&quot;checkbox&quot; name=&quot;updateOrderCheck&quot; id=&quot;updateOrderCheck&quot; ng-model=&quot;order.autoUpdateProductVers&quot; ng-disabled=&quot;processReadOnly()&quot; ng-change=&quot;setModifiedFlagTrue()&quot; ng-true-value=&quot;Y&quot; ng-false-value=&quot;N&quot;&gt;</pre><pre class="prettyprint linenums lang-js">主要是通过 ng-true-value=&quot;Y&quot; ng-false-value=&quot;N&quot; 来改变</pre><br /><br /></div><div style="text-align: left;"><br /><br /><br /></div>\r\n', 'wengel', '2014-12-22 11:04:13', '', '2015-08-14 15:12:49'),
	(208, 79, 'angularjs-如何使用$broadcast', 'icon-m-file', 'open', 0, 1, '<p style="text-align: center;"><strong>angularjs-如何使用$broadcast</strong></p><p style="text-align: left;"><strong>举个oes的info dialog的例子一：</strong></p><p></p><pre class="prettyprint linenums lang-js">function oesInfoEvent(title, type, message ){\r\n	return {\r\n		event : {\r\n			name : \'OES_INFO\',\r\n			data : {\r\n				title :  title ? title : \'OES Info\',\r\n				type : type ? type : \'info\',\r\n				message :  message ? message : \'\'\r\n			}\r\n		}\r\n	};\r\n};\r\n\r\n// 全领域范围广播\r\n<span style="background-color: rgb(255, 255, 153);">$rootScope.$broadcast</span>(\'OES_END\', oesInfoEvent(\'Error\', \'error\', data));</pre><br /><pre class="prettyprint linenums lang-js">//局部接收,在另一个js中\r\n  	<span style="background-color: rgb(102, 255, 255);">$scope.$on</span>(\'OES_END\', function(event, data){\r\n    	 $(\'#progress-dialog\').modal(\'hide\');\r\n    	 emitEvent($rootScope, data);\r\n     });\r\n</pre><br /><pre class="prettyprint linenums lang-js">function emitEvent(scope, data){\r\n	 if(data &amp;&amp; data.event &amp;&amp; angular.isFunction(scope.$broadcast)){\r\n		 var event = data.event;\r\n		 if(event.name){\r\n			 scope.$broadcast(event.name, event.data);\r\n		 }\r\n	 };\r\n};</pre><br /><p><strong><span style="font-weight: bold;">例子二： oes dialog js整个文件</span><br />&lt;oes-dialog.directive.js&gt;</strong></p><p></p><pre class="prettyprint linenums lang-js">/**\r\n * Oes dialogs - info, warning, error.s\r\n */\r\n\r\nfunction normalizeMessage(message){\r\n	if(angular.isArray(message)){\r\n		return message.join(\'&lt;br&gt;\');\r\n	}else{\r\n		return message;\r\n	}\r\n};\r\n\r\norderApp.controller(\'ProgressController\',[\'$scope\', \'$rootScope\', \'CommonService\', function($scope, $rootScope, commonService){\r\n		\r\n		$scope.imgUrl = commonService.getGuiUrl(\'/resources/styles/img/spinner.gif\');\r\n	\r\n  	$scope.$on(\'OES_IN_PROGRESS\', function(event, data){\r\n   	 $(\'#progress-dialog\').modal(\'show\');\r\n   	 emitEvent($rootScope, data);\r\n    });\r\n   \r\n  	$scope.$on(\'OES_END\', function(event, data){\r\n    	 $(\'#progress-dialog\').modal(\'hide\');\r\n    	 emitEvent($rootScope, data);\r\n     });\r\n   \r\n }]).controller(\'InfoController\',[\'$scope\', \'$rootScope\', \'CommonService\', function($scope, $rootScope, commonService){\r\n		\r\n	$scope.title = &quot;Info!&quot;;\r\n	$scope.info = &quot;&quot;;\r\n	\r\n	var setClassesByType = function(type){\r\n		$(\'#info-dialog\').removeClass(\'alert-success\');\r\n		$(\'#info-dialog\').removeClass(\'alert-info\');\r\n		$(\'#info-dialog\').removeClass(\'alert-error\');\r\n		$(\'#info-dialog\').removeClass(\'alert-warning\');\r\n		if(type === \'error\'){\r\n			$(\'#info-dialog\').addClass(\'alert-error\');\r\n		}else if(type === \'info\'){\r\n			$(\'#info-dialog\').addClass(\'alert-info\');\r\n		}else if(type === \'success\'){\r\n			$(\'#info-dialog\').addClass(\'alert-success\');\r\n		}else if(type === \'warning\'){ // -w\r\n			$(\'#info-dialog\').addClass(\'alert-warning\');\r\n		}\r\n	};\r\n	\r\n 	<span style="background-color: rgb(255, 255, 153);">$scope.$on(\'OES_INFO\', function(event, data){</span>\r\n 		 $scope.title = data.title ?  data.title : $scope.title;\r\n 		 $scope.info = normalizeMessage(data.message);\r\n 		 setClassesByType(data.type);\r\n  	 $(\'#info-dialog\').show();\r\n  	 angular.element(&quot;#info-dialog&quot;).scope().$apply();\r\n  	<span style="background-color: rgb(255, 255, 153);"> emitEvent($rootScope, data);</span>\r\n   });\r\n  \r\n 	$scope.$on(\'OES_CLOSE_INFO\', function(event, data){\r\n   	 $(\'#info-dialog\').hide();\r\n   	 emitEvent($rootScope, data);\r\n  });\r\n  \r\n}]).directive(\'infoDialog\', [\'CommonService\', function(commonService){\r\n	 return {\r\n		 restrict : \'E\',\r\n		 templateUrl : commonService.getGuiUrl(\'/resources/js/oes/directives/\') + \'info-dialog.html\'\r\n	 };\r\n 	}]\r\n ).directive(\'progressDialog\', [\'CommonService\', function(commonService){\r\n	 return {\r\n		 restrict : \'E\',\r\n		 templateUrl : commonService.getGuiUrl(\'/resources/js/oes/directives/\') + \'progress-dialog.html\'\r\n	 };\r\n 	}]\r\n );</pre><br />广播请求调用的地方,在另一js文件<p></p><p></p><pre class="prettyprint linenums lang-js">	/**\r\n	 * display error message\r\n	 */\r\n	$scope.displayError = function(message, title){\r\n		<span style="background-color: rgb(255, 255, 153);">$rootScope.$broadcast(\'OES_INFO\', {</span>\r\n					title : title ? title : \'Error\',\r\n					type : \'error\',\r\n					message : message\r\n		});\r\n	};\r\n	\r\n	\r\n	$scope.displayMessage = function(message, title){\r\n		$rootScope.$broadcast(\'OES_INFO\', {\r\n					title : title ? title : \'Info\',\r\n					type : \'success\',\r\n					message : message\r\n		});\r\n	};</pre><br /><br /><p></p><p><br /></p><p></p>\r\n', 'wengel', '2015-03-10 16:15:19', '', '2015-08-14 15:12:49'),
